<html>	<head>		<title>Borower Details</title>		<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.css">		</head>		<body>			<h1>Details of the Borrower</h1>											<div>						<p><a href="../../home.php"><button class='ui labled teal button'><i class="home icon"></i></button></a></p>		<p><a href=create_input.php><button class='ui labled olive button'>BORROW BOOKS</button></a></p>				</div><?php	require_once('db_connect.php');	$connect = mysqli_connect( HOST, USER, PASS, DB )		or die("Can not connect");	$results = mysqli_query( $connect, "SELECT	l.member_id, m.first_name,l.isbn, b.title,	l.hire_date,l.submission_date		FROM lends l	join book b 	on l.isbn = b.isbn	join member m 	on l.member_id = m.member_id		order by member_id ASC;" )			or die("Can not execute query");	echo "<table class='ui celled striped unstackable collapsing table'> \n";	echo "<thead><tr><th>MEMBER ID</th> <th>MEMBER NAME</th>	<th>ISBN</th> <th>TITLE</th> <th>TOTAL BOOK</th> 	<th>HIRE DATE</th> <th>SUBMISSION DATE</th> 	<th>DELETE</th> <th>UPDATE</th></tr></thead><tbody> \n";	while( $rows = mysqli_fetch_array( $results ) ) {		extract( $rows );		echo "<tr>";		echo "<td> $member_id </td>";		echo "<td> $first_name </td>";		echo "<td> $isbn </td>";		echo "<td> $title </td>";		echo "<td> $member_id </td>";		echo "<td> $hire_date </td>";		echo "<td> $submission_date </td>";		echo "<td> <a href = 'delete.php?id=$member_id&isbn=$isbn'> Delete </a> </td>";		echo "<td> <a href = 'update_input.php?hire_date=$hire_date		&submission_date=$submission_date		&isbn=$isbn		&member_id=$member_id'> Edit </a> </td>";		echo "</tr> \n";	}	echo "</tbody></table> \n";	?>		</body></html>